function horario() {
    const horarioAPI = new Date()
    const hora = document.querySelectorAll('.horario.hora')
    const minutos = document.querySelectorAll('.horario.minutos')
    const year = document.querySelectorAll('.horario.year')
    const tiempo = document.querySelectorAll('.horario.tiempo')
    const mes = document.querySelectorAll('.horario.mes')
    const mesArray = ['Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio', 'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre']

    hora.forEach(el => {
        setInterval(() => el.textContent = horarioAPI.getHours(), 1000)
    })
    
    minutos.forEach(el => {
        setInterval(() => el.textContent = horarioAPI.getMinutes(), 1000)
    })
    
    year.forEach(el => {
        el.textContent = horarioAPI.getFullYear()
    })
    
    tiempo.forEach(el => {
        setInterval(() => {
            el.textContent = `${horarioAPI.getHours()}:${horarioAPI.getMinutes()}:${horarioAPI.getSeconds()}`
        }, 1000)
    })

    mes.forEach(el => {
        el.textContent =mesArray[horarioAPI.getMonth()]
    })
}

module.exports.horario = horario